import { GET_ERROR, REVIEW_RESTAURANT } from "../action/type";
const initialState = {
    reviews: [],
}

export default function (state = initialState, action){
    const {type, payload} = action;
    switch(type){
        case REVIEW_RESTAURANT:
            return {
                ...state,
                reviews: payload,
            }
        case GET_ERROR:
        default: return state;
    }
}
