import { w3cwebsocket } from 'websocket';
import {useState, useEffect} from 'react'


function ChatMessage() {
    let token = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ0b2tlbl90eXBlIjoicmVmcmVzaCIsImV4cCI6MTY5OTk1ODU4MiwiaWF0IjoxNjk5MzUzNzgyLCJqdGkiOiIwYTA0N2MxNTY4M2U0MzE4OGVmODUwNmQ3MWY4OGY2NyIsInVzZXJfaWQiOjF9.CiS-o2IAtERRMLasLYJerO9qDEAr0XTX3llx3uQZIZI';
    const client = new w3cwebsocket(`ws://127.0.0.1:8000/chat/?token=${token}`);
    const [message, setMessage] = useState('');
    const [chatHistory, setChatHistory] = useState([]);

    useEffect(() => {
        client.onopen = () => {
          console.log('WebSocket Client Connected');
        };
    
        client.onmessage = (message) => {
          const messageData = JSON.parse(message.data);

          setChatHistory((prevChatHistory) => [
            ...prevChatHistory,
            { text: messageData.message, type: 'received' },
          ]);
        };

        client.onerror = (e) => {
          console.log(e);
        };
    
        client.onclose = () => {
          console.log('WebSocket Client Closed');
        };

      }, []);
    
      const sendMessage = () => {
        if (message.trim() === '') return;

        // Send the message to the WebSocket server
        client.send(JSON.stringify({ message }));

        // Update the chat history with the sent message
        setChatHistory((prevChatHistory) => [
          ...prevChatHistory,
          { text: message, type: 'sent' },
        ]);

        // Clear the message input field
        setMessage('');
      };
    return ( 
        <div>
            <nav className='nav-header'>
                <i class="fas fa-list"></i>
                <i class="fa-solid fa-user"></i>
            </nav>
            <nav className='nav-middle'>
              <div className="view-link">
                <p className='top'>Chat Messages</p>
                <p>Home</p>
                <i class="fas fa-chevron-right"></i>
                <p>Chat Messages</p>
              </div>
            </nav>
            {/* <div className='chat-container'>
              <div className='chat-history'>
                {chatHistory.map((chat, index) => (
                  <div key={index} className={chat.type}>
                    {chat.text}
                  </div>
                ))}
              </div>
              <div className='chat-input'>
                <input
                  type='text'
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  placeholder='Type your message...'
                />
                <button onClick={sendMessage}>Send</button>
              </div>
              </div> */}
            <main className="content" style={{ marginTop: "150px" }}>
        <div className="container p-0">
          <h1 className="h3 mb-3">Messages</h1>
          <div className="card">
            <div className="row g-0">
              <div className="col-12 col-lg-5 col-xl-3 border-right">
              <div className="px-4 ">
                  <div className="d-flfex align-itemfs-center">
                    <div className="flex-grow-1 d-flex align-items-center mt-2">
                      <input
                        type="text"
                        className="form-control my-3"
                        placeholder="Search..."
                        // onChange={handleSearchChange}
                        name='username'

                      />
                      <button className='ml-2' style={{border:"none", borderRadius:"50%"}}><i className='fas fa-search'></i></button>
                    </div>
                  </div>
                </div>
                
                {/* {users.map((user, index) => 
                  <Link 
                    to={"/inbox/" + user.id}
                    className="list-group-item list-group-item-action border-0"
                  >

                    <small><div className="badge bg-success float-right text-white"></div></small>
                    <div className="d-flex align-items-start">
                      <img src={user.image} className="rounded-circle mr-1" alt="1" width={40} height={40}/>
                    
                      <div className="flex-grow-1 ml-3">
                         {user.full_name}  

                        <div className="small">
                           <small><i className='fas fa-envelope'> Send Message</i></small>
                        </div>
                      </div>
                    </div>
                    </Link>
                )} */}
                
                <hr className="d-block d-lg-none mt-1 mb-0" />
              </div>
              
            </div>
          </div>
        </div>
      </main>
      </div>

     );
}

export default ChatMessage;
