import { w3cwebsocket } from 'websocket';
import {useState, useEffect, useMemo} from 'react';
import notfoundimg from '../../assets/images/not_found.png';
import {NavLink} from 'react-router-dom'
import ContentChat from './ContentChat/ContentChat';
import {useDispatch, useSelector} from 'react-redux'
import { friend_chat } from '../../action/auth';


function ChatMessage() {
    // let token = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ0b2tlbl90eXBlIjoicmVmcmVzaCIsImV4cCI6MTY5OTk1ODU4MiwiaWF0IjoxNjk5MzUzNzgyLCJqdGkiOiIwYTA0N2MxNTY4M2U0MzE4OGVmODUwNmQ3MWY4OGY2NyIsInVzZXJfaWQiOjF9.CiS-o2IAtERRMLasLYJerO9qDEAr0XTX3llx3uQZIZI';
    // const client = useMemo(() => new w3cwebsocket(`ws://127.0.0.1:8000/chat/?token=${token}`), [token]);
    
    const [query, setQuery] = useState('');
    const [users, setUsers] = useState([]);
    const [displayUser, setDisplayUser] = useState([]);
    const [connectUser, setConnectUser] = useState('');
    const [friend, setFriend] = useState({});
    const [isConnect, setIsConnect] = useState(false);

    const friends = useSelector(state=>state.auth.friends);
    const dispatch = useDispatch();


    useEffect(()=>{
      async function getFriend(){
        const action  = await friend_chat(localStorage.getItem('iduser'));
        dispatch(action);
    }
    getFriend();
    }, [])

    useEffect(()=>{
      setDisplayUser(friends);
    }, [friends])

    // useEffect(() => {
    //     client.onopen = () => {
    //       console.log('WebSocket Client Connected');
    //         client.send(JSON.stringify({
    //           source: 'search',
    //           query: ""
    //         }));
    //         if(client.OPEN) setIsConnect(true);
    //     };
    //     client.onmessage = (event) => {
    //       const data = JSON.parse(event.data);
    //       console.log(data);
    //       setUsers([...data])
    //     };
    //     client.onerror = (e) => {
    //       console.log(e);
    //     };
    //     client.onclose = () => {
    //       console.log('WebSocket Client Closed');
    //     };
    //   }, [client]);

    //   useEffect(()=>{
    //     setDisplayUser([...users]);
    //     if(users && users.length>0){
    //       setConnectUser(users[0].username);
    //       setFriend(users[0]);
    //     }
    //   }, [users])

      function handleSearch(e){
        setQuery(e.target.value);
      }

      function handleConnect(e){
        // setConnectUser(JSON.parse(e.currentTarget.getAttribute('data-user')));
        const newFriend = friends.filter(user=>user.username === e.currentTarget.getAttribute('data-user'))[0];
        setFriend(newFriend);
        // setConnectUser(e.currentTarget.getAttribute('data-user'));
      }

      // useEffect(()=>{
      //   if(isConnect === true){
      //     if(connectUser !== null)
      //         client.send(JSON.stringify({
      //           source: 'connect',
      //           query: connectUser
      //         }));
      //   }
      // }, [connectUser, isConnect])

      useEffect(()=>{
        if (query !== '') {
          const lowercaseQuery = query.toLowerCase();
          const filteredUsers = friends.filter(user => friends.username.toLowerCase().includes(lowercaseQuery));
          setDisplayUser(filteredUsers);
        } else {
          setDisplayUser(friends);
        }
      }, [query])
      console.log(displayUser);
      
    return ( 
        <div>
            <nav className='nav-header'>
                <i class="fas fa-list"></i>
                <i class="fa-solid fa-user"></i>
            </nav>
            <nav className='nav-middle'>
              <div className="view-link">
                <p className='top'>Chat Messages</p>
                <p><NavLink to="/restaurant">Home</NavLink></p>
                <i class="fas fa-chevron-right"></i>
                <p>Chat Messages</p>
              </div>
            </nav>
            <main className="content">
              <div className="container p-0">
                <div className="card">
                  <div className="row g-0">
                    <div className="col-12 col-lg-5 col-xl-4 border-right">
                      <div className="px-4 d-none d-md-block">
                        <div className="d-flex align-items-center">
                          <div className="flex-grow-1">
                            <h3 className='mt-3'>Contact list</h3>
                            <input type="text" className="form-control my-3" placeholder="Search..." onChange={handleSearch}/>
                          </div>
                        </div>
                      </div>
                      <div className='px-4 d-none d-md-block'>
                        {displayUser && displayUser.length>0?displayUser.map((user, index)=>{
                          return (
                            <div className="list-group-item list-group-item-action border-0 mb-2" 
                            data-user={user.username}
                            onClick={handleConnect} key={index}>
                            <div className="d-flex align-items-start">
                              <img src={`${user.image}`}
                                className="rounded-circle avatar"
                                width={40}
                                height={40}/>
                              <div className='flex-grow-1'>
                                <h6 className='m-0'>{user.username}</h6>
                                <div className="small">
                                  {/* <span className="fas fa-circle chat-online" />  */}
                                  Online
                                </div>
                              </div>
                            </div>
                          </div>
                          )
                        }):<div className='d-flex flex-column align-items-center'>
                            <img src={notfoundimg} className='center mt-5'/>
                            <h3 className='text-secondary mt-3'>No results were found</h3>
                          </div>
                        }
                      </div>
                      <hr className="d-block d-lg-none mt-1 mb-0" />
                    </div>
                    {friend?<ContentChat 
                    // client = {client}
                      friend={friend}/>:""}
                  </div>
                </div>
              </div>
            </main>
      </div>
    )
}

export default ChatMessage;
