import { w3cwebsocket } from 'websocket';
import {useState, useEffect, useMemo} from 'react'


function ChatMessage() {
    let token = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ0b2tlbl90eXBlIjoicmVmcmVzaCIsImV4cCI6MTY5OTk1ODU4MiwiaWF0IjoxNjk5MzUzNzgyLCJqdGkiOiIwYTA0N2MxNTY4M2U0MzE4OGVmODUwNmQ3MWY4OGY2NyIsInVzZXJfaWQiOjF9.CiS-o2IAtERRMLasLYJerO9qDEAr0XTX3llx3uQZIZI';
    const client = useMemo(() => new w3cwebsocket(`ws://127.0.0.1:8000/chat/?token=${token}`), [token]);
    const [message, setMessage] = useState('');
    const [query, setQuery] = useState('hoa ngao');
    // const data_user = useRef([]);
    const [users, setUsers] = useState([]);
    const [chatHistory, setChatHistory] = useState([]);

    useEffect(() => {
        client.onopen = () => {
          console.log('WebSocket Client Connected');
            client.send(JSON.stringify({
              source: 'search',
              query: query
            }));
        };
    
        client.onmessage = (message) => {
          const messageData = JSON.parse(message.data);
          setUsers([...messageData])
          // data_user.current = messageData;
          // handleUpdatedData(messageData);
        };

        client.onerror = (e) => {
          console.log(e);
        };
    
        client.onclose = () => {
          console.log('WebSocket Client Closed');
        };

      }, [client]);

      // useEffect(()=>{
      //   setUsers(data_user.current);
      //   // if (data_user.current.length > 0) {
      //     console.log(data_user.current, "ok");
      //   // }
      // }, [data_user.current])


      const sendMessage = () => {
        if (message.trim() === '') return;

        // Send the message to the WebSocket server
        client.send(JSON.stringify({ message }));

        // Update the chat history with the sent message
        setChatHistory((prevChatHistory) => [
          ...prevChatHistory,
          { text: message, type: 'sent' },
        ]);

        // Clear the message input field
        setMessage('');
      };

      const handleUpdatedData = (data) => {
        // data_user.current = [...data];
        
        // console.log(data_user.current);
      };

      // console.log(users);
      
    return ( 
        <div>
            <nav className='nav-header'>
                <i class="fas fa-list"></i>
                <i class="fa-solid fa-user"></i>
            </nav>
            <nav className='nav-middle'>
              <div className="view-link">
                <p className='top'>Chat Messages</p>
                <p>Home</p>
                <i class="fas fa-chevron-right"></i>
                <p>Chat Messages</p>
              </div>
            </nav>
            <main className="content">
              <div className="container p-0">
                <div className="card">
                  <div className="row g-0">
                    <div className="col-12 col-lg-5 col-xl-4 border-right">
                      <div className="px-4 d-none d-md-block">
                        <div className="d-flex align-items-center">
                          <div className="flex-grow-1">
                            <input
                              type="text"
                              className="form-control my-3"
                              placeholder="Search..."
                            />
                          </div>
                        </div>
                      </div>
                      <div className='px-4 d-none d-md-block'>
                        {users && users.length>0?users.map((user, index)=>{
                          return (
                            <a
                          href="#"
                          className="list-group-item list-group-item-action border-0 mb-3"
                        >
                          <div className="d-flex align-items-start">
                            <img
                              src="https://bootdey.com/img/Content/avatar/avatar5.png"
                              className="rounded-circle mr-c"
                              alt="Vanessa Tucker"
                              width={40}
                              height={40} 
                            />
                            <div className='flex-grow-1'>
                              Vanessa Tucker
                              <div className="small">
                                <span className="fas fa-circle chat-online" /> Online
                              </div>
                            </div>
                          </div>
                        </a>
                          )
                        }):""}
                      </div>
                      <hr className="d-block d-lg-none mt-1 mb-0" />
                    </div>
                    <div className="col-12 col-lg-7 col-xl-8" style={{borderLeft: '1px solid #ccc'}}>
                      <div className="py-2 px-4 border-bottom d-none d-lg-block">
                        <div className="d-flex align-items-center py-1">
                          <div className="position-relative">
                            <img
                              src="https://bootdey.com/img/Content/avatar/avatar3.png"
                              className="rounded-circle mr-1"
                              alt="Sharon Lessman"
                              width={40}
                              height={40}
                            />
                          </div>
                          <div className="flex-grow-1 pl-3">
                            <strong>Sharon Lessman</strong>
                            <div className="text-muted small">
                              <em>Online</em>
                            </div>
                          </div>
                        </div>
                      </div>
                      <div className="position-relative">
                        <div className="chat-messages p-4">
                          <div className="chat-message-right pb-4">
                            <div>
                              <img
                                style={{ marginRight: '10px' }}
                                src="https://bootdey.com/img/Content/avatar/avatar1.png"
                                className="rounded-circle"
                                alt="Chris Wood"
                                width={40}
                                height={40}
                              />
                              <div className="text-muted small text-nowrap mt-2">
                                2:33 am
                              </div>
                            </div>
                            <div className="flex-shrink-1 bg-light rounded py-2 px-3 mr-3">
                              Lorem ipsum dolor sit amet, vis erat denique in, dicunt
                              prodesset te vix.
                            </div>
                          </div>
                          <div className="chat-message-left pb-4">
                            <div>
