import { 
    GET_ERROR, 
    REVIEW_RESTAURANT ,
    ORDER_HISTORY,
    GET_TABLE,
    GET_DISHES,
    ORDER_DETAIL,
    ADD_DISH,
    DELETE_DISH,
    UPDATE_DISH,
} from "../action/type";
import axios from "axios";

const config = {
    headers: {
        "Content-type": "application/json",
    }
}

export const getReviews = async (rid)=>{
    const body = JSON.stringify({rid})
    
    try{
        const res = await axios.get(`http://127.0.0.1:8000/api/reviews-restaurant/${rid}/`, body, config)
        if(res.data.success === true){
            const result = {
                type: REVIEW_RESTAURANT,
                payload: res.data.data
            }
            return result
        }
        else{
            return {
                type: GET_ERROR,
                payload: res.data.message
            }
        }
        
    } catch(e){
        return {
            type: GET_ERROR,
            payload: e
        }
    }
}

export const getHistoryOrder = async (rid)=>{
    const body = JSON.stringify({rid})
    
    try{
        const res = await axios.get(`http://127.0.0.1:8000/api/order-restaurant/${rid}/`, body, config)
        if(res.data.success === true){
            const result = {
                type: ORDER_HISTORY,
                payload: res.data.data
            }
            return result
        }
        else{
            return {
                type: GET_ERROR,
                payload: res.data.message
            }
        }
        
    } catch(e){
        return {
            type: GET_ERROR,
            payload: e
        }
    }
}

export const getDishes = async (rid)=>{
