import React from 'react';
import FullCalendar from '@fullcalendar/react';
import resourceTimelinePlugin from '@fullcalendar/resource-timeline';

// Import CSS cho FullCalendar (tùy chọn)
import '@fullcalendar/core/main.css';
import '@fullcalendar/resource-timeline/main.css';


function ManageOrder() {
    const resources = [
        { id: 'resource1', title: 'Resource 1' },
        { id: 'resource2', title: 'Resource 2' },
        // Thêm tài nguyên khác nếu cần
      ];
    
      // Sự kiện lịch
      const events = [
        {
          id: 'event1',
          title: 'Event 1',
          start: '2023-10-10T08:00:00',
          end: '2023-10-10T10:00:00',
          resourceId: 'resource1', // Ánh xạ sự kiện với tài nguyên
        },
        {
          id: 'event2',
          title: 'Event 2',
          start: '2023-10-10T09:00:00',
          end: '2023-10-10T11:00:00',
          resourceId: 'resource2', // Ánh xạ sự kiện với tài nguyên
        },
        // Thêm sự kiện khác nếu cần
      ];
    
      return (
        <div>
          <FullCalendar
            plugins={[resourceTimeGridPlugin]}
            initialView="resourceTimeGridWeek" // Chọn chế độ xem
            resources={resources} // Danh sách tài nguyên
            events={events} // Sự kiện lịch
          />
        </div>
      );
}

export default ManageOrder;
