import {useState, useEffect} from 'react';
import notfoundimg from '../../assets/images/not_found.png';
import {NavLink} from 'react-router-dom'
import ContentChat from './ContentChat/ContentChat';
import {useDispatch, useSelector} from 'react-redux'
import { friend_chat } from '../../action/auth';

function ChatMessage() {
    const [query, setQuery] = useState('');
    const [displayUser, setDisplayUser] = useState([]);
    const [friend, setFriend] = useState({});
    const friends = useSelector(state=>state.auth.friends);
    const dispatch = useDispatch();

    useEffect(()=>{
      async function getFriend(){
        const action  = await friend_chat(localStorage.getItem('iduser'));
        dispatch(action);
    }
    getFriend();
    }, [])

    useEffect(()=>{
      setDisplayUser(friends);
      // console.log(friends);
      if(friends.length>0){
        // console.log("ok")
        if(friends[0].msg_sender.username === localStorage.getItem("username")) setFriend(friends[0].msg_receiver);
        else setFriend(friends[0].msg_sender);
      }
        // console.log(friends[0].msg_sender.username)
      
    }, [friends])

    function handleSearch(e){
      setQuery(e.target.value);
    }

    // function handleConnect(e){
    //   const newFriend = {};
    //   // console.log(friends)
    //   // friends.filter(user=>user.msg_sender.username === )[0];
    //   friends.map((item, index)=>{
    //     if(item.msg_sender.username === e.currentTarget.getAttribute('data-user')) newFriend=item.msg_receiver;
    //     else newFriend=item.msg_sender;
    //   });
    //   setFriend(newFriend);
    // }

    function handleConnect(e) {
      const clickedUsername = e.currentTarget.getAttribute('data-user');
    
      // Use find to get the first friend that matches the clicked username
      const newFriend = friends.find((item) => {
        return item.msg_sender.username === clickedUsername || item.msg_receiver.username === clickedUsername;
      });
    
      // Check if a friend was found before setting it
      if (newFriend) {
        // Determine if the clicked username corresponds to msg_sender or msg_receiver
        const friendToSet = (newFriend.msg_sender.username === clickedUsername) ? newFriend.msg_sender : newFriend.msg_receiver;
    
        // Set the friend using the setFriend function
        setFriend(friendToSet);
      } else {
        // Handle the case where no friend was found
        console.error(`Friend not found for username: ${clickedUsername}`);
      }
      console.log(friend);
    }

    useEffect(()=>{
      if (query !== '') {
        const lowercaseQuery = query.toLowerCase();
        const filteredUsers = friends.filter(user => user.username.toLowerCase().includes(lowercaseQuery));
        setDisplayUser(filteredUsers);
      } else {
        setDisplayUser(friends);
      }
    }, [query])
    console.log(friend);
      
    return ( 
        <div>
            <nav className='nav-header'>
                <i class="fas fa-list"></i>
                <i class="fa-solid fa-user"></i>
            </nav>
            <nav className='nav-middle'>
              <div className="view-link">
                <p className='top'>Chat Messages</p>
                <p><NavLink to="/restaurant">Home</NavLink></p>
                <i class="fas fa-chevron-right"></i>
                <p>Chat Messages</p>
              </div>
            </nav>
            <main className="content">
              <div className="container p-0">
                <div className="card">
                  <div className="row g-0">
                    <div className="col-12 col-lg-5 col-xl-4 border-right">
                      <div className="px-4 d-none d-md-block">
                        <div className="d-flex align-items-center">
                          <div className="flex-grow-1">
                            <h3 className='mt-3'>Contact list</h3>
                            <input type="text" className="form-control my-3" placeholder="Search..." onChange={handleSearch}/>
                          </div>
                        </div>
                      </div>
                      <div className='px-4 d-none d-md-block'>
                        {displayUser && displayUser.length>0?displayUser.map((item, index)=>{
                          let user = {};
                          if(item.msg_sender.username === localStorage.getItem("username")) user = item.msg_receiver;
                          else user = item.msg_sender
                          return (
                            <div className="list-group-item list-group-item-action border-0 mb-2" 
                            data-user={user.username} onClick={handleConnect} key={index}>
                            <div className="d-flex align-items-start">
                              <img src={`${user.image}`}
                                className="rounded-circle avatar"
                                width={40}
                                height={40}/>
                              <div className='flex-grow-1'>
                                <h6 className='m-0'>{user.username}</h6>
                                <div className="small">
                                  {/* <span className="fas fa-circle chat-online" />  */}
                                </div>
                              </div>
                            </div>
                          </div>
                          )
                        }):<div className='d-flex flex-column align-items-center'>
                            <img src={notfoundimg} className='center mt-5'/>
                            <h3 className='text-secondary mt-3'>No results were found</h3>
                          </div>
                        }
                      </div>
                      <hr className="d-block d-lg-none mt-1 mb-0" />
                    </div>
                    <ContentChat friend={friend}/>
                  </div>
                </div>
              </div>
            </main>
      </div>
    )
}

export default ChatMessage;
