import React from 'react';
import FullCalendar from '@fullcalendar/react';
import resourceTimelinePlugin from '@fullcalendar/resource-timeline';

// Import CSS cho FullCalendar (tùy chọn)
import '@fullcalendar/core/main.css';
import '@fullcalendar/resource-timeline/main.css';


function ManageOrder() {
    // Danh sách các phòng
  const resources = [
    { id: 'room1', title: 'Phòng 1' },
    { id: 'room2', title: 'Phòng 2' },
    // Thêm phòng khác nếu cần
  ];

  return (
    <div>
      <FullCalendar
        plugins={[resourceTimelinePlugin]}
        initialView="resourceTimelineDay" // Chọn chế độ xem Timeline cho một ngày với hàng là phòng và cột là giờ
        resources={resources} // Danh sách các phòng
        events={[
          {
            title: 'Sự kiện 1',
            start: '2023-10-10T08:00:00',
            end: '2023-10-10T10:00:00',
            resourceId: 'room1', // Ánh xạ sự kiện với phòng
          },
          {
            title: 'Sự kiện 2',
            start: '2023-10-10T09:00:00',
            end: '2023-10-10T11:00:00',
            resourceId: 'room2', // Ánh xạ sự kiện với phòng
          },
          // Thêm sự kiện khác nếu cần
        ]}
      />
    </div>
  );
}

export default ManageOrder;
