import { GET_ERROR, REVIEW_RESTAURANT } from "../action/type";
import axios from "axios";

const config = {
    headers: {
        "Content-type": "application/json",
    }
}

export const getReviews = async (rid)=>{
    const body = JSON.stringify({rid})
    
    try{
        const res = await axios.post(`http://127.0.0.1:8000/api/reviews-restaurant/${rid}/`, body, config)
        if(res.data.success === true){
            alert(res.data.message)
            const result = {
                type: REVIEW_RESTAURANT,
                payload: res.data.data
            }
            return result
        }
        else{
            alert(res.data.message)
            return {
                type: GET_ERROR,
                payload: res.data.message
            }
        }
        
    } catch(e){
        alert("Error!")
        return {
            type: GET_ERROR,
            payload: e
        }
    }
}
