import { w3cwebsocket } from 'websocket';
import {useState, useEffect} from 'react'


function ChatMessage() {
    let token = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ0b2tlbl90eXBlIjoiYWNjZXNzIiwiZXhwIjoxNjk5MzQzNDEzLCJpYXQiOjE2OTkzNDI1MTMsImp0aSI6IjcxYmM4NWZmN2QwYTRiZTk4YjFkMjAxZjQ4ODhhZjhiIiwidXNlcl9pZCI6MX0.r7RQYLZFxiMaTaQm6rC78UopHkXQeGI8UTAUCp_vxQE';
    const client = new w3cwebsocket(`ws://127.0.0.1:8000/ws/chat/?token=${token}`);
    const [message, setMessage] = useState('');
    const [chatHistory, setChatHistory] = useState([]);

    useEffect(() => {
        client.onopen = () => {
          console.log('WebSocket Client Connected');
        };
    
        client.onmessage = (message) => {
          console.log('WebSocket Client chat');
        };

        client.onerror = (e) => {
          console.log(e);
        };
    
        client.onclose = () => {
          console.log('WebSocket Client Closed');
        };
    
        // return () => {
        //   client.close();
        // };
      }, []);
    
      const sendMessage = () => {
        if (message.trim() === '') return;
        client.send(JSON.stringify({ message }));
        setMessage('');
      };
    return ( 
        <div>
            <nav className='nav-header'>
                <i class="fas fa-list"></i>
                <i class="fa-solid fa-user"></i>
            </nav>
            <nav className='nav-middle'>
                <p className='top'>Chat Messages</p>
                <p>Home</p>
                <i class="fas fa-chevron-right"></i>
                <p>Chat Messages</p>
            </nav>
        </div>
     );
}

export default ChatMessage;
