import { w3cwebsocket } from 'websocket';
import {useState, useEffect} from 'react'


function ChatMessage() {
    let token = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ0b2tlbl90eXBlIjoicmVmcmVzaCIsImV4cCI6MTcwMzYwMTQ1NiwiaWF0IjoxNjk5MjgxNDU2LCJqdGkiOiJlZTEzODdiOGUxNDg0MGZmYWYxNmZiNDY2NTJlMGI4YiIsInVzZXJfaWQiOjEsImZ1bGxfbmFtZSI6IiIsInVzZXJuYW1lIjoidmluaG5nbyIsImVtYWlsIjoibmdvdjY3NjlAZ21haWwuY29tIiwiYmlvIjoiIiwiaW1hZ2UiOiJkZWZhdWx0LmpwZyIsInZlcmlmaWVkIjpmYWxzZX0.BGOcw_8MxdD2KEZMq_IoxOedr41CkLXo3LybZOo1qVM';
    const client = new w3cwebsocket(`ws://127.0.0.1:8000/chat/?token=${token}`);
    const [message, setMessage] = useState('');
    const [chatHistory, setChatHistory] = useState([]);

    useEffect(() => {
        client.onopen = () => {
          console.log('WebSocket Client Connected');
        };
    
        client.onmessage = (message) => {
          const messageData = JSON.parse(message.data);

          setChatHistory((prevChatHistory) => [
            ...prevChatHistory,
            { text: messageData.message, type: 'received' },
          ]);
        };

        client.onerror = (e) => {
          console.log(e);
        };
    
        client.onclose = () => {
          console.log('WebSocket Client Closed');
        };
        
      }, []);
    
      const sendMessage = () => {
        if (message.trim() === '') return;

        // Send the message to the WebSocket server
        client.send(JSON.stringify({ message }));

        // Update the chat history with the sent message
        setChatHistory((prevChatHistory) => [
          ...prevChatHistory,
          { text: message, type: 'sent' },
        ]);

        // Clear the message input field
        setMessage('');
      };
    return ( 
        <div>
            <nav className='nav-header'>
                <i class="fas fa-list"></i>
                <i class="fa-solid fa-user"></i>
            </nav>
            <nav className='nav-middle'>
                <p className='top'>Chat Messages</p>
                <p>Home</p>
                <i class="fas fa-chevron-right"></i>
                <p>Chat Messages</p>
            </nav>
            <div className='chat-container'>
              <div className='chat-history'>
                {chatHistory.map((chat, index) => (
                  <div key={index} className={chat.type}>
                    {chat.text}
                  </div>
                ))}
              </div>
              <div className='chat-input'>
                <input
                  type='text'
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  placeholder='Type your message...'
                />
                <button onClick={sendMessage}>Send</button>
              </div>
              </div>
      </div>

     );
}

export default ChatMessage;
